import { Typography,Grid,FormControlLabel,TextField,Checkbox,LinearProgress, Link, makeStyles, CssBaseline, AppBar, Toolbar, Paper, Stepper, Step, StepLabel, Button } from '@material-ui/core';
import React from 'react';
import { createTheme } from '@material-ui/core/styles';
import theme from '../theme';
import {ThemeProvider} from '@material-ui/core';
import {
  createStyles,
  alpha,
  Theme,
  withStyles,
} from '@material-ui/core/styles';


interface OtpProps {
    changeState:(newstate:number)=>void;
}


  const useStyles = makeStyles((theme) => ({
    appBar: {
      position: 'relative',
      width:'100vw',
      '& label.Mui-focused': {
        color: '#000',
      },
      '& .MuiInput-underline:after': {
        borderBottomColor: '#000',
      },
      '& .MuiOutlinedInput-root': {
        '& fieldset': {
          borderColor: '#000',
        },
        '&:hover fieldset': {
          borderColor: '#000',
        },
        '&.Mui-focused fieldset': {
          borderColor: '#000',
        },
      },
    },
    palette: {
      primary: {
        // Purple and green play nicely together.
        main: '#000',
      },
      secondary: {
        // This is green.A700 as hex.
        main: '#000',
      },
    },
    TextField: {

      width:'5vw'
    },
    layout: {
      width: 'auto',
      marginLeft: theme.spacing(2),
      marginRight: theme.spacing(2),
      [theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
        width: 600,
        marginLeft: 'auto',
        marginRight: 'auto',
      },
    },
    paper: {
      marginTop: theme.spacing(3),
      marginBottom: theme.spacing(3),
      padding: theme.spacing(2),
      [theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
        marginTop: theme.spacing(6),
        marginBottom: theme.spacing(6),
        padding: theme.spacing(3),
        width:'50vw',
        height:'40vw',
        marginLeft: 'auto',
        marginRight: 'auto',

      },
    },
    stepper: {
      padding: theme.spacing(3, 0, 5),
    },
    buttons: {
      display: 'flex',
      justifyContent: 'flex-end',
      width:'5vw',
    },
    button: {
      marginTop: theme.spacing(3),
      marginLeft: theme.spacing(1),
      width:'5vw',
    },
  }));



const Otp: React.FC<OtpProps> = ({changeState }: OtpProps) => {
    const classes = useStyles();

    return(
      <ThemeProvider theme={theme}>
        <React.Fragment>
      <CssBaseline />
      <AppBar position="absolute" color="default" className={classes.appBar}>
      <Toolbar>
        <Grid container justify="flex-end" style={{ padding: 0 }}>
        <Button
              type="submit"
              variant="outlined"
              style={{backgroundColor: '#fff', color: '#000'}}
              onClick={()=>changeState(2)}
            >
              Sign Out
            </Button>
        </Grid>

        </Toolbar>
      </AppBar>

        <Paper className={classes.paper}>
          Business Setup....................................................02/04
        <LinearProgress variant="determinate" value={50} />

        <Typography variant="h6" color="inherit" noWrap>
            Your Business Details.
          </Typography>
          What is your businesses registration number?
          <br></br>
          <TextField id="outlined-basic" fullWidth label="Business Registration Number" variant="outlined" />

      <br></br>
          <React.Fragment>
          <Button
              type="submit"
              variant="outlined"
              style={{backgroundColor: '#fff', color: '#000'}}
              onClick={()=>changeState(2)}
            >
              Skip Step
            </Button>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              onClick={()=>changeState(2)}
            >
              Continue
            </Button>
          </React.Fragment>
        </Paper>

    </React.Fragment>
    </ThemeProvider>
    );
}


export default Otp;
