    layout: {
      width: 'auto',
      marginLeft: theme.spacing(2),
      marginRight: theme.spacing(2),
      [theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
        width: 600,
        marginLeft: 'auto',
        marginRight: 'auto',
      },
    },
    paper: {
      marginTop: theme.spacing(3),
      marginBottom: theme.spacing(3),
      padding: theme.spacing(2),
      [theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
        marginTop: theme.spacing(6),
        marginBottom: theme.spacing(6),
        padding: theme.spacing(3),
        width:'50vw',
        height:'30vw',
        marginLeft: 'auto',
        marginRight: 'auto',

      },
    },
    stepper: {
      padding: theme.spacing(3, 0, 5),
    },
    buttons: {
      display: 'flex',
      justifyContent: 'flex-end',
    },
    button: {
      marginTop: theme.spacing(3),
      marginLeft: theme.spacing(1),
      width:'5vw',
    },
  }));



const Otp: React.FC<OtpProps> = ({changeState }: OtpProps) => {
    const classes = useStyles();
    const [username, setUserName] = React.useState("");
    const [first_name, setFirstName] = React.useState("");
    const [surname, setSurname] = React.useState("");
    const [phone_number, setPhoneNumber] = React.useState("");
    const [email_address, setEmailAddress] = React.useState("");
    const [role, setRole] = React.useState("");

    const changeUserName = (event: React.ChangeEvent<any>) => {
      setUserName(event.target.value);
  };
  const changeFirstName = (event: React.ChangeEvent<any>) => {
    setFirstName(event.target.value);
  };
  const changeSurname = (event: React.ChangeEvent<any>) => {
    setSurname(event.target.value);
};
const changePhoneNumber = (event: React.ChangeEvent<any>) => {
  setPhoneNumber(event.target.value);
};
const changeEmailAddress = (event: React.ChangeEvent<any>) => {
  setEmailAddress(event.target.value);
};
const changeRole = (event: React.ChangeEvent<any>) => {
  setRole(event.target.value);
};


    return(
      <ThemeProvider theme={theme}>
        <React.Fragment >
      <CssBaseline />
      <AppBar position="absolute" color="default" className={classes.appBar}>
        <Toolbar>
        <Grid container justify="flex-end" style={{ padding: 0 }}>
        <Button
              type="submit"
              variant="outlined"
              style={{backgroundColor: '#fff', color: '#000'}}
              onClick={()=>changeState(2)}
            >
              Sign Out
            </Button>
        </Grid>

        </Toolbar>
      </AppBar>

        <Paper className={classes.paper}>
        <Typography variant="h6" color="inherit" noWrap>
            Enter your OTP
          </Typography>
          An OTP has been sent to name@gmail.com
          <br></br>
          Please verify your account before continuing
          <br></br>
          <div style={{ display: 'inline-flex' }}>
            <div>
            <TextField 
             style = {{width: 60}}
                variant="outlined"
                margin="normal"
                required
                fullWidth
                name="phone_number"
                value={phone_number}
                label="phone_number"
                id="phone_number"
                autoComplete="phone_number"
                onChange={changePhoneNumber}


                />            </div>
            <div style={{ alignSelf: 'center' }}>
            <TextField style = {{width: 60}} id="outlined-basic"  variant="outlined" />            </div>
            <div>
            <TextField style = {{width: 60}} id="outlined-basic"  variant="outlined" />            </div>
            <div style={{ alignSelf: 'center' }}>
            <TextField style = {{width: 60}} id="outlined-basic"  variant="outlined" />            </div>
        </div>
          <React.Fragment>

          </React.Fragment>
          <br></br>
          If your inbox is empty please click here
          <br></br>
          Or check your spam box
        </Paper>


    </React.Fragment>
    </ThemeProvider>

    );
}


export default Otp;
