import { Typography, Link, makeStyles, CssBaseline, AppBar, Toolbar, Paper, Stepper, Step, StepLabel, Button } from '@material-ui/core';
import React from 'react';
;


interface OtpProps {
    changeState:(newstate:number)=>void;
}

function Copyright() {
    return (
      <Typography variant="body2" color="textSecondary" align="center">
        {'Copyright © '}
        <Link color="inherit" href="https://material-ui.com/">
          Your Website
        </Link>{' '}
        {new Date().getFullYear()}
        {'.'}
      </Typography>
    );
  }

  const useStyles = makeStyles((theme) => ({
    appBar: {
      position: 'relative',
      width:'100vw'
    },
    layout: {
      width: 'auto',
      marginLeft: theme.spacing(2),
      marginRight: theme.spacing(2),
      [theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
        width: 600,
        marginLeft: 'auto',
        marginRight: 'auto',
      },
    },
    paper: {
      marginTop: theme.spacing(3),
      marginBottom: theme.spacing(3),
      padding: theme.spacing(2),
      [theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
        marginTop: theme.spacing(6),
        marginBottom: theme.spacing(6),
        padding: theme.spacing(3),
        width:'50vw',
        height:'50vw',
        marginLeft: 'auto',
        marginRight: 'auto',

      },
    },
    stepper: {
      padding: theme.spacing(3, 0, 5),
    },
    buttons: {
      display: 'flex',
      justifyContent: 'flex-end',
    },
    button: {
      marginTop: theme.spacing(3),
      marginLeft: theme.spacing(1),
      width:'5vw',
    },
  }));



const Otp: React.FC<OtpProps> = ({changeState }: OtpProps) => {
    const classes = useStyles();

    return(
        <React.Fragment>
      <CssBaseline />
      <AppBar position="absolute" color="default" className={classes.appBar}>
        <Toolbar>
          <Typography variant="h6" color="inherit" noWrap>
            Company name
          </Typography>
          <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              onClick={()=>changeState(2)}
            >
              Sign Out
            </Button>
        </Toolbar>
      </AppBar>

        <Paper className={classes.paper}>
        <Typography variant="h6" color="inherit" noWrap>
            Enter your OTP
          </Typography>
          An OTP has been sent to name@gmail.com
          <br></br>
          Please verify your account before continuing
          <React.Fragment>
          <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              onClick={()=>changeState(2)}
            >
              OK
            </Button>
          </React.Fragment>
        </Paper>
        <Copyright />

    </React.Fragment>
    );
}


export default Otp;
