import React from 'react';
import theme from '../theme';
import {ThemeProvider,FormControl,Radio,RadioGroup,FormLabel,Divider} from '@material-ui/core';


interface OtpProps {
    changeState:(newstate:number)=>void;
}


  const useStyles = makeStyles((theme) => ({
    root:{
      '& label.Mui-focused': {
      },
      "& .MuiLinearProgress-colorPrimary": {
        backgroundColor: "#E8E8E8"
      },
      "& .MuiLinearProgress-barColorPrimary": {
        backgroundColor: "#009F51"
      },
    },
    appBar: {
      position: 'relative',
      width:'100vw'
    },
    TextField: {

      width:'5vw'
    },
    layout: {
      width: 'auto',
      marginLeft: theme.spacing(2),
      marginRight: theme.spacing(2),
      [theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
        width: 600,
        marginLeft: 'auto',
        marginRight: 'auto',
      },
    },
    paper: {
      marginTop: theme.spacing(3),
      marginBottom: theme.spacing(3),
      padding: theme.spacing(2),
      [theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
        marginTop: theme.spacing(6),
        marginBottom: theme.spacing(6),
        padding: theme.spacing(3),
        width:'50vw',
        height:'790px',
        marginLeft: 'auto',
        marginRight: 'auto',
        borderRadius: '0%',

      },
    },
    stepper: {
      padding: theme.spacing(3, 0, 5),
    },
    buttons: {
      display: 'flex',
      borderRadius: '0%',
      justifyContent: 'flex-end',
      width:'5vw',
    },

    button: {
      marginTop: theme.spacing(3),
      borderRadius: '0%',
      marginLeft: theme.spacing(1),
      width:'5vw',
    },
  }));



const Otp: React.FC<OtpProps> = ({changeState }: OtpProps) => {
    const classes = useStyles();

    return(
      <ThemeProvider theme={theme}>
        <React.Fragment>
      <CssBaseline />
      <AppBar position="absolute" color="default" className={classes.appBar}>
      <Toolbar>
        <Grid container justify="flex-end" style={{ padding: 0 }}>
        <Button
              type="submit"
              variant="outlined"
              style={{backgroundColor: '#fff', color: '#000',padding: "0.5vh 1.8vw",textTransform: 'none'}}
              onClick={()=>changeState(2)}
            >
              Sign Out
            </Button>
        </Grid>

        </Toolbar>
      </AppBar>

        <Paper className={classes.paper}>
        <Grid >Business Setup</Grid><Grid container justify="flex-end">03/04</Grid>
          <LinearProgress color="secondary"  variant="determinate" value={75} />
<Box style={{padding:"3vw" }}>
        <Typography variant="h6" color="inherit"  noWrap>
           <b> Your business size?</b>
          </Typography>
          <Box style={{height:"0.5vw" }}></Box>




          We are here for you no matter how big or small. What's your business size?
                <br></br>

      <Box border={1}>
      <Radio
         style={{ color:"#000"}}
        value="a"
        name="radio-button-demo"
        inputProps={{ 'aria-label': 'A' }}
      />
Below R000.00
      </Box>
      <Box border={1}>
      <Radio
         style={{ color:"#000"}}
        value="a"
        name="radio-button-demo"
        inputProps={{ 'aria-label': 'A' }}
      />
Between R000.00-R000.00
      </Box>
      <Box border={1}>
      <Radio
         style={{ color:"#000"}}
        value="a"
        name="radio-button-demo"
        inputProps={{ 'aria-label': 'A' }}
      />
Below R000.00
      </Box>


      <br></br>
      <TextField id="outlined-basic" fullWidth label="Custom Amount" variant="outlined" />

      {/* <Divider width="110%" /> */}
      <Box height="2%"></Box>

      <Grid container justify="flex-end">
          <React.Fragment>

          <Button
              type="submit"
              variant="outlined"
              style={{padding: "1vh 3.8vw",textTransform: 'none',backgroundColor: '#fff', color: '#000', border:"none",outline:"none"  }}
              onClick={()=>changeState(4)}
            >
              <u>Skip Step</u>
            </Button>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              style={{padding: "1vh 3.8vw",textTransform: 'none'  }}
              onClick={()=>changeState(4)}
            >
              Continue
            </Button>
          </React.Fragment>
          </Grid>
        </Box>
        </Paper>

    </React.Fragment>

    </ThemeProvider>

    );
}


export default Otp;
